import axios from "axios"
import { useEffect, useState } from "react"


// remember to export the component, or it cannot be called.
export default function News() {

    // create state for store data
    const [data, setData] = useState([])

    // must have this for inspection
    useEffect(() => {
        getNews()
    }, [])

    // get news from our django rest framework
    // attention!! you need to write CORS allowed in the settings of django
    function getNews() {
        const url = "http://127.0.0.1:8000/api/news"
        axios.get(url).then((res) => {
            console.log(res)
        }).catch((err) => {
            console.log(err)
        })
    }
    return (
        <>
            <article >

                <img src="https://ichef.bbci.co.uk/news/976/cpsprodpb/D952/production/_123443655_a6232613-2c93-49cc-8997-ead88933326d.jpg" alt="" id="featuredico" />
                <h1>Example Title</h1>
                <p>Example Announce Text</p>
                <a>Read Me</a>

            </article>
        </>

    )
}
